import React from 'react';
import { Card, Row, Col } from 'antd';
import './Style.css';

const { Meta } = Card;

const testimonials = [
  {
    name: 'Marvin McKinney',
    title: 'Business Owner',
    description: 'Since switching to Swift Revel, I finally have peace of mind regarding my business finances. With their secure platform, I trust my data\'s protection completely.',
    image: 'https://randomuser.me/api/portraits/men/10.jpg', // Replace with actual image source
  },
  {
    name: 'Jacob Jones',
    title: 'Startup Founder',
    description: 'As a budding startup, we sought a financial solution that was simple, secure, and scalable. Swift Revel checked all the boxes, enabling focus on growth.',
    image: 'https://randomuser.me/api/portraits/men/20.jpg', // Replace with actual image source
  },
  {
    name: 'Theresa Webb',
    title: 'Financial Manager',
    description: 'Swift Revel empowers me to manage my business finances with confidence and ease. Their integrated tax solutions ensure I\'m always tax compliant.',
    image: 'https://randomuser.me/api/portraits/women/30.jpg', // Replace with actual image source
  },
];

const Testimonials = () => (
  <div className="testimonials">
    <h2>What Users Say</h2>
    <p>Testimonials that speak louder than words! Customer stories that light up our day.</p>
    <Row gutter={[16, 16]}>
      {testimonials.map((testimonial, index) => (
        <Col xs={24} sm={24} md={8} key={index}>
          <Card
            hoverable
            className="testimonial-card"
            cover={<img alt={testimonial.name} src={testimonial.image} />}
          >
            <Meta title={testimonial.name} description={testimonial.title} />
            <p className="testimonial-description">{testimonial.description}</p>
          </Card>
        </Col>
      ))}
    </Row>
  </div>
);

export default Testimonials;
